import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaEnvelope, FaLinkedin, FaWhatsapp } from 'react-icons/fa';

const Contact = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [showPopup, setShowPopup] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const subject = encodeURIComponent(`Message from ${formData.name}`);
    const body = encodeURIComponent(`Email: ${formData.email}\n\n${formData.message}`);
    const mailtoLink = `mailto:roshithakommana18@gmail.com?subject=${subject}&body=${body}`;
    window.location.href = mailtoLink;
    setShowPopup(true);
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  return (
    <section id="contact" className="text-white px-6 py-16 bg-dark relative">
      <div className="max-w-6xl mx-auto grid md:grid-cols-2 gap-12">

        {/* Left: Form */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          <h2 className="text-3xl font-bold mb-6 text-primary">Get in Touch With Me</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              name="name"
              placeholder="Your Full Name"
              required
              className="w-full p-3 rounded bg-gray-800 text-white focus:outline-none focus:ring-2 focus:ring-primary"
              onChange={handleChange}
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              required
              className="w-full p-3 rounded bg-gray-800 text-white focus:outline-none focus:ring-2 focus:ring-primary"
              onChange={handleChange}
            />
            <textarea
              name="message"
              rows="5"
              placeholder="Your Message"
              required
              className="w-full p-3 rounded bg-gray-800 text-white focus:outline-none focus:ring-2 focus:ring-primary"
              onChange={handleChange}
            ></textarea>
            <button type="submit" className="bg-primary px-6 py-3 rounded shadow hover:scale-105 transition-transform">Send</button>
          </form>
        </motion.div>

        {/* Right: Contact Cards */}
        <motion.div
          className="flex flex-col gap-6"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
          viewport={{ once: true }}
        >
          <motion.div
            className="bg-gray-900 p-6 rounded-lg shadow-md flex items-center gap-4"
            whileHover={{ scale: 1.05 }}
          >
            <FaEnvelope className="text-primary text-3xl" />
            <div>
              <h4 className="text-lg font-semibold">Email</h4>
              <a href="mailto:roshithakommana18@gmail.com" className="text-gray-400 hover:text-primary">roshithakommana18@gmail.com</a>
            </div>
          </motion.div>

          <motion.div
            className="bg-gray-900 p-6 rounded-lg shadow-md flex items-center gap-4"
            whileHover={{ scale: 1.05 }}
          >
            <FaLinkedin className="text-primary text-3xl" />
            <div>
              <h4 className="text-lg font-semibold">LinkedIn</h4>
              <a href="https://linkedin.com/in/roshitha-kommana" target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-primary">
                linkedin.com/in/roshitha-kommana
              </a>
            </div>
          </motion.div>

          <motion.div
            className="bg-gray-900 p-6 rounded-lg shadow-md flex items-center gap-4"
            whileHover={{ scale: 1.05 }}
          >
            <FaWhatsapp className="text-primary text-3xl" />
            <div>
              <h4 className="text-lg font-semibold">WhatsApp</h4>
              <a href="https://wa.me/916302680957" target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-primary">
                +91 63026 80957
              </a>
            </div>
          </motion.div>
        </motion.div>
      </div>

      {/* Glassmorphism Popup */}
      {showPopup && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50 z-50">
          <div className="backdrop-blur-md bg-white/10 text-white p-8 rounded-xl shadow-lg border border-white/20">
            <h3 className="text-xl font-semibold mb-4">Sent Successfully!</h3>
            <button
              onClick={handlePopupClose}
              className="bg-primary px-4 py-2 rounded hover:bg-opacity-80"
            >
              OK
            </button>
          </div>
        </div>
      )}
    </section>
  );
};

export default Contact;
