import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
} from 'react-router-dom';
import React from 'react';
import Hero from './components/Hero';
import About from './components/About';
import Education from './components/Education';
import Skills from './components/Skills';
import ProjectsCertificates from './components/ProjectsCertificates';
import ProjectDetails from './components/ProjectDetails';
import Contact from './components/Contact';
import Navbar from './components/Navbar';

const AppRoutes = () => {
  const location = useLocation();

  return (
  <Routes location={location} key={location.pathname}>
    <Route
      path="/"
      element={
        <div
        className="min-h-screen bg-gradient-to-br from-[#636FA4] to-[#E8CBC0] text-white flex items-center justify-center px-4 py-16">

          <Hero />
          <About />
          <Education />
          <Skills />
          <ProjectsCertificates />
          <Contact />
        </div>
      }
    />
    <Route path="/projects/:id" element={<ProjectDetails />} />
  </Routes>
);

};

function App() {
  return (
    <Router>
      <Navbar />
      <AppRoutes />
    </Router>
  );
}

export default App;
