import React from 'react';
import { motion } from 'framer-motion';

import { DiCode } from 'react-icons/di';
import { FaDatabase, FaCheckCircle } from 'react-icons/fa';
import { FiTool } from 'react-icons/fi';
import { MdLightbulb } from 'react-icons/md';
import { GiSkills } from 'react-icons/gi';

const skillsData = [
  {
    category: 'Languages',
    icon: <DiCode size={18} className="text-purple-500" />,
    skills: ['Java', 'Python', 'C++', 'C', 'HTML', 'CSS', 'JavaScript', 'Tailwind CSS', 'React.js'],
  },
  {
    category: 'Backend & Databases',
    icon: <FaDatabase size={18} className="text-purple-500" />,
    skills: ['Firebase', 'MongoDB', 'MySQL'],
  },
  {
    category: 'Tools & Libraries',
    icon: <FiTool size={18} className="text-purple-500" />,
    skills: ['OpenCV', 'Tesseract OCR', 'Git', 'VS Code'],
  },
  {
    category: 'Concepts',
    icon: <MdLightbulb size={18} className="text-purple-500" />,
    skills: ['OOP', 'DBMS', 'Data Structures', 'Machine Learning', 'Big Data', 'UI/UX Principles'],
  },
  {
    category: 'Key Strengths',
    icon: <GiSkills size={18} className="text-purple-500" />,
    skills: ['Problem Solving', 'Team Collaboration', 'Active Listener', 'Time Management'],
  },
];

// Slide in directions for wave effect
const directions = ['-50px', '50px', '-50px', '50px', '0px'];

const getVariant = (index) => {
  const axis = index % 2 === 0 ? 'x' : 'y';
  const offset = directions[index % directions.length];
  return {
    hidden: { [axis]: offset },
    visible: {
      [axis]: '0px',
      transition: {
        delay: index * 0.2,
        duration: 0.6,
        ease: 'easeOut',
      },
    },
  };
};

const Skills = () => {
  return (
    <section
      id="skills"
      className="max-w-5xl mx-auto py-16 px-4 bg-[#0b0f19] text-white select-none"
    >
      <h2 className="text-3xl font-extrabold mb-12 text-center text-purple-400 tracking-wide">
        Skills & Strengths
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {skillsData.map(({ category, icon, skills }, index) => (
          <motion.div
            key={category}
            className="bg-[#1e2235] rounded-lg p-5 cursor-pointer border border-transparent hover:border-purple-600 transition-all"
            initial="hidden"
            whileInView="visible"
            exit="hidden"
            viewport={{ once: false, amount: 0.2 }}
            variants={getVariant(index)}
            whileHover={{
              scale: 1.04,
              boxShadow: '0 0 12px 2px rgba(124, 58, 237, 0.5)',
              transition: { duration: 0.3 },
            }}
          >
            <div className="flex items-center mb-4 space-x-3">
              <div className="p-2 bg-purple-600 rounded-full flex justify-center items-center text-white shadow-sm">
                {icon}
              </div>
              <h3 className="text-xl font-semibold text-purple-300 tracking-wide">
                {category}
              </h3>
            </div>

            <div className="flex flex-wrap gap-2">
              {skills.map((skill) => (
                <span
                  key={skill}
                  className="flex items-center space-x-1 bg-purple-800/25 text-purple-200 px-3 py-0.5 rounded-full text-xs font-medium select-text"
                >
                  <FaCheckCircle className="text-purple-400" size={12} />
                  <span>{skill}</span>
                </span>
              ))}
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
