import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';
import { FolderKanban, BadgeCheck } from 'lucide-react'; // You can change to any Lucide icons you prefer


const projects = [
  {
    id: 'donateease',
    name: 'DonateEase – Web-Based Donation Platform',
    shortDesc: 'Web app enabling donations across multiple categories with QR-based payments and dynamic forms.',
    imageUrl: '/assets/donate.png',
    liveDemo: 'https://donateease-2f624.web.app/',
    github: 'https://github.com/Roshitha-Kommana/DonateEase',
    technologies: ['HTML', 'JavaScript', 'Firebase', 'Cloudinary'],
    keyFeatures: [
      'Built a web application enabling users to donate across 4 categories (money, clothes, books, electronics)',
      'Integrated QR-based UPI payments and Cloudinary API for secure screenshot storage',
      'Enabled conditional dynamic forms with validations and Firestore-based data persistence',
      'Achieved 100% functional donation tracking and improved submission accuracy',
    ],
    date: 'Dec 2024 - Feb 2025',
  },
  {
    id: 'smartocr',
    name: 'Smart OCR – Automated Text Extraction System',
    shortDesc: 'OCR pipeline for scanned PDFs and images with image preprocessing and Tesseract OCR.',
    imageUrl: 'https://raw.githubusercontent.com/Roshitha-Kommana/smart_ocr/main/assets/ocr_sample.png',
    liveDemo: null,
    github: 'https://github.com/Roshitha-Kommana/smart_ocr',
    technologies: ['Python', 'OpenCV', 'PyMuPDF', 'Tesseract'],
    keyFeatures: [
      'Built an OCR pipeline to extract text from scanned PDFs and images',
      'Applied preprocessing (grayscale, denoising, thresholding) to improve accuracy',
      'Used Tesseract to convert images into structured text for document digitization',
    ],
    date: 'N/A',
  },
  {
    id: 'helmetdetection',
    name: 'Helmet Detection Using YOLO – Real-Time Object Detection',
    shortDesc: 'Real-time helmet detection system using YOLO to ensure safety compliance in industrial and traffic environments.',
    imageUrl: '/assets/helmet_detection.png',
    liveDemo: null,
    github: 'https://github.com/Roshitha-Kommana/HelmetDetectionYOLO',
    technologies: ['YOLO', 'Python', 'OpenCV', 'Deep Learning', 'Darknet', 'TensorFlow', 'PyTorch'],
    keyFeatures: [
      'Developed a real-time helmet detection system using YOLO object detection algorithm',
      'Processed live video streams to classify individuals based on helmet usage',
      'Implemented bounding box annotations to differentiate helmeted and non-helmeted persons',
      'Optimized detection pipeline for low latency and high accuracy in real-time',
      'Designed for deployment in industrial, construction, and traffic surveillance systems',
    ],
    date: 'Nov 2024 - Dec 2024',
  },
  {
    id: 'flappybirdclone',
    name: 'Flappy Bird Clone – Game Development with JavaScript',
    shortDesc: 'A browser-based Flappy Bird game clone built using HTML5, CSS3, and vanilla JavaScript for smooth gameplay and responsive controls.',
    imageUrl: '/assets/flappy_bird.png',
    liveDemo: 'https://roshitha-kommana.github.io/FlappyBird-JS/',
    github: 'https://github.com/Roshitha-Kommana/FlappyBird-JS',
    technologies: ['JavaScript', 'HTML5', 'CSS3', 'Canvas API'],
    keyFeatures: [
      'Implemented smooth player controls and collision detection using JavaScript',
      'Created parallax background and animated bird mechanics for a dynamic feel',
      'Built a scoring system and obstacle spawning logic with increasing difficulty',
      'Used Canvas API for rendering graphics in real-time',
      'Designed to work across all major browsers with responsive layout',
    ],
    date: 'Oct 2024',
  },
];

const certificates = [
  { id: 1, imageUrl: '/certificates/leadership.jpg', link: 'https://www.credly.com/badges/xxxxxxxx' },
  { id: 2, imageUrl: '/certificates/cloudcomputing.jpg', link: '' },
  { id: 3, imageUrl: '/assets/certificate3.png', link: '' },
  { id: 4, imageUrl: '/assets/certificate4.png', link: '' },
  { id: 5, imageUrl: '/assets/certificate5.png', link: '' },
];

const badges = [
  { id: 1, imageUrl: '/badges/aws.jpg', link: 'https://www.credly.com/badges/980dc4f7-80d5-4322-b59e-8920ac1db095/public_url' },
  { id: 2, imageUrl: '/badges/ibm.jpg', link: 'https://www.credly.com/badges/ibm-badge-url' },
  { id: 3, imageUrl: '/badges/coursera.jpg', link: 'https://www.credly.com/badges/coursera-badge-url' },
];


const MediaCarousel = ({ items }) => {
  const [selected, setSelected] = useState(null);
  const [startIndex, setStartIndex] = useState(0);
  const itemsPerPage = 3;

  const visibleItems = items.slice(startIndex, startIndex + itemsPerPage);

  const nextSlide = () => {
    if (startIndex + itemsPerPage < items.length) {
      setStartIndex(startIndex + 1);
    }
  };

  const prevSlide = () => {
    if (startIndex > 0) {
      setStartIndex(startIndex - 1);
    }
  };

  const [hasAnimated, setHasAnimated] = useState(false);
  useEffect(() => {
    const timeout = setTimeout(() => setHasAnimated(true), 100);
    return () => clearTimeout(timeout);
  }, []);

  return (
    <div className="relative w-full flex flex-col items-center">
      <div className="flex justify-center items-center space-x-4">
        {startIndex > 0 && (
          <button
            onClick={prevSlide}
            className="bg-purple-300 hover:bg-purple-500 text-white rounded-full p-1 text-sm"
          >
            «
          </button>
        )}

        <div
          className={`flex space-x-6 transform transition-transform duration-700 ease-out ${
            hasAnimated ? 'translate-x-0 opacity-100' : 'translate-x-20 opacity-0'
          }`}
        >
          {visibleItems.map((item) => (
            <div
              key={item.id}
              className="relative w-64 h-52 flex-shrink-0 group bg-[#0b0f19] border border-[#3f3f3f] rounded-lg px-4 py-2"
            >
              <div className="w-full h-full flex items-center justify-center">
                <img
                  src={item.imageUrl}
                  alt={`Item ${item.id}`}
                  className="max-w-[100%] max-h-[100%] object-contain rounded-md"
                />
              </div>

              <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-60 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                {item.link ? (
                  <button
                    onClick={() => window.open(item.link, '_blank')}
                    className="text-white text-lg font-semibold hover:underline transition"
                  >
                    View Badge
                  </button>
                ) : (
                  <span className="text-white text-lg font-semibold">No Link</span>
                )}
              </div>
            </div>
          ))}
        </div>

        {startIndex + itemsPerPage < items.length && (
          <button
            onClick={nextSlide}
            className="bg-purple-300 hover:bg-purple-500 text-white rounded-full p-1 text-sm"
          >
            »
          </button>
        )}
      </div>

      {selected && (
        <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50">
          <button
            onClick={() => setSelected(null)}
            className="absolute top-6 right-6 text-white text-3xl hover:text-purple-400"
          >
            &times;
          </button>
          <img
            src={selected.imageUrl}
            alt="Full View"
            className="max-w-[90vw] max-h-[90vh] rounded-xl"
          />
        </div>
      )}
    </div>
  );
};

const ProjectsCertificates = () => {
  const [activeTab, setActiveTab] = useState('projects');
  const [showAll, setShowAll] = useState(false);
  const visibleProjects = showAll ? projects : projects.slice(0, 3);

  return (
    <section className="max-w-7xl mx-auto px-6 py-20 bg-[#0b0f19] text-white">
      <motion.h2
        className="text-4xl font-extrabold mb-12 text-center text-purple-400"
        initial={{ opacity: 0, y: -30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        Projects & Certificates
      </motion.h2>

      {/* Tabs */}
      <div className="flex justify-center space-x-6 mb-14">
        {[
          { key: 'projects', label: 'Projects', icon: <FolderKanban className="w-4 h-4 mr-2" /> },
          { key: 'certificates', label: 'Certificates', icon: <BadgeCheck className="w-4 h-4 mr-2" /> },
          { key: 'badges', label: 'Badges', icon: <BadgeCheck className="w-4 h-4 mr-2 text-yellow-400" /> },
        ].map((tab) => (
          <motion.button
            key={tab.key}
            onClick={() => {
              setActiveTab(tab.key);
              setShowAll(false);
            }}
            className={`px-6 py-2 rounded-xl font-semibold transition-all duration-300 ease-in-out flex items-center
              ${activeTab === tab.key
                ? 'bg-purple-700/80 border border-purple-400 text-white shadow-lg'
                : 'bg-purple-900/30 border border-purple-600 text-purple-300 hover:bg-purple-800/40'}`}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.97 }}
          >
            {tab.icon}
            <span>{tab.label}</span>
          </motion.button>
        ))}
      </div>

      {/* Content */}
      {activeTab === 'projects' ? (
        <>
          <motion.div
            className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8"
            initial="hidden"
            whileInView="visible"
            viewport={{ once: false }}
          >
            {visibleProjects.map((proj, i) => (
              <motion.div
                key={proj.id}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: i * 0.1 }}
                className="bg-[#1e2235] rounded-xl shadow-md p-4 flex flex-col items-center hover:shadow-purple-600 transition-shadow"
              >
                <div className="w-full h-40 overflow-hidden rounded-md bg-gray-900 flex items-center justify-center p-2">
                  <img src={proj.imageUrl} alt={proj.name} className="w-full h-full object-cover rounded-md" />
                </div>
                <h3 className="mt-4 text-lg font-semibold text-purple-300 text-center">{proj.name}</h3>
                <p className="mt-2 text-sm text-purple-200 text-center">{proj.shortDesc}</p>
                <div className="mt-4 w-full flex justify-between items-center">
                  {proj.liveDemo ? (
                    <a href={proj.liveDemo} target="_blank" rel="noopener noreferrer" className="text-purple-400 hover:text-purple-600 font-semibold">
                      Live Demo
                    </a>
                  ) : (
                    <span className="text-purple-700 text-sm italic">No Demo</span>
                  )}
                  <Link to={`/projects/${proj.id}`} className="text-purple-400 hover:text-purple-600 font-semibold flex items-center space-x-1">
                    <span>Full Details</span>
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}>
                      <path strokeLinecap="round" strokeLinejoin="round" d="M9 5l7 7-7 7" />
                    </svg>
                  </Link>
                </div>
              </motion.div>
            ))}
          </motion.div>

          {!showAll && projects.length > 3 && (
            <div className="flex justify-center mt-10">
              <motion.button
                onClick={() => setShowAll(true)}
                className="text-purple-300 hover:text-purple-500 text-lg flex items-center space-x-2"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <span>See More</span>
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 animate-bounce" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2} fill="none">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" />
                </svg>
              </motion.button>
            </div>
          )}
        </>
      ) : activeTab === 'certificates' ? (
        <motion.div initial={{ opacity: 0, x: 50 }} whileInView={{ opacity: 1, x: 0 }} transition={{ duration: 0.6 }}>
          <MediaCarousel items={certificates} />
        </motion.div>
      ) : (
        <motion.div initial={{ opacity: 0, x: 50 }} whileInView={{ opacity: 1, x: 0 }} transition={{ duration: 0.6 }}>
          <MediaCarousel items={badges} />
        </motion.div>
      )}
    </section>
  );
};

export default ProjectsCertificates;
